import React, { useEffect } from 'react';
import './App.css';
import Home from './routes/home'
import Game from './routes/game'
import Options from './routes/options'
import Credits from './routes/credits'
import StarBackground from './assets/components/starBackground'
import file from './assets/sound/sappheiros-embrace.mp3'

import {
  useLocation,
  Switch,
  Link,
  Route
} from "react-router-dom";

function App() {
  
  useEffect(() => {
    let audio = document.querySelector('audio')
    const audioctx = new AudioContext()
    const track = audioctx.createMediaElementSource(audio)
    track.connect(audioctx.destination);
      // check if context is in suspended state (autoplay policy)
      if (audioctx.state === 'suspended') {
        audioctx.resume();
    }
      
    audio.play()
    // // play or pause track depending on state
    // if (audioctx.dataset.playing === 'false') {
    //     audio.play();
    //     audioctx.dataset.playing = 'true';
    // } else if (this.dataset.playing === 'true') {
    //     audio.pause();
    //     audioctx.dataset.playing = 'false';
    // }
  })

  let location = useLocation()['pathname']

  return (
    <div className="App">

      <StarBackground />
      <audio src={require('./assets/sound/sappheiros-embrace.mp3')} controls loop autoPlay/>
             
      <header>

        { location !== '/'  &&
    
            <Link to='/' className='headerbutton'>
              <span className="material-icons">arrow_back</span>
            </Link>
}

        { location === '/game'  &&
          <div className='headerbutton'>
              <span className="material-icons">pause</span>
          </div>
        } 

      </header>

      <Switch>
        <Route exact path="/" children={<Home />} />
        <Route path="/game" children={<Game />} />
        <Route path="/options" children={<Options />} />
        <Route path="/credits" children={<Credits />} />
      </Switch>

      <footer>
          <a href='https://github.com/KevvKo/jump-and-run-js'>@KevvKo</a>
        </footer>
    </div>
  );
}

export default App;
