import { game } from '../../scripts/game'

const initialState= {
    game: game
}

function canvasReducer( state = initialState, action) {

    if(action.type === 'game/Start'){
        state.game.start()
    }

    if(action.type === 'game/Stop'){
        state.game.start()
    }

    return state;
}

export default gameReducer
