import {game} from './game'

function isDown(direction){

    return direction === 'w' || 'UP' ||  's' || 'DOWN' || 'a' || 'LEFT' || 'd' || 'RIGHT'
}

export default function handleInput(e){

    // UP
    if(isDown(e)){
        game.spaceship.y =  game.spaceship.y -1
    }

    // DOWN
    if(isDown(e)){
        game.spaceship.y =  game.spaceship.y +1
    }

    // LEFT
    if(isDown(e)){
        game.spaceship.x =  game.spaceship.y -1
    }

    // RIGHT
    if(isDown(e)){
        game.spaceship.x =  game.spaceship.y +1
    }
}
