import { game } from '../../scripts/game'

const initialState= {
    game: new Game()
}

export default function gameReducer( state = initialState, action) {

    if(action.type === 'game/Start'){
        state.game.init()
    }

    if(action.type === 'game/Stop'){
        state.game.stop()
    }

    return state;
}
