class Game{

    constructor(){}

    init(){

        this.canvas = document.getElementById('gameBoard')
        this.context = this.canvas.getContext('2d')
        
        this.start()
    }   

    start(){
        window.requestAnimationFrame(()=> this.update())
    }

    update(){

        this.draw()
        window.requestAnimationFrame(()=> this.update())
    }

    draw(){
        this.context.fillStyle ="red"
        this.context.fillRect(10,10,10,10)
    }
}
const game = new Game()
export {game}
