const initialState = {
    keys: []
}

export default function keysReducer(state = initialState, action){

    if(action.type === "keys/addKeys"){

        return {
           keys: [
                action.key = true, 
                ...state.keys
            ]
        }
    } 

    if(action.type === "keys/removeKey"){

        return {
           keys: [
                action.key = false, 
                ...state.keys
            ]
        }
    }

    return state;
}
