import React, { useEffect } from 'react';
import './App.css';
import Home from './routes/home'
import Game from './routes/game'
import Options from './routes/options'
import Credits from './routes/credits'
import StarBackground from './assets/components/starBackground'

import {
  useLocation,
  Switch,
  Link,
  Route
} from "react-router-dom";

function toggleMusic(musicPlays){
  let button = document.querySelector('.playMusic')
  let audioElement = document.querySelector('audio')

  if(musicPlays){
    audioElement.stop()
    button.children[0].innerText = 'music_note'
  }
  audioElement.play()
  button.children[0].innerText = 'music_note'

}

function App() {
  const musicPlays = false

  let location = useLocation()['pathname']

  return (
    <div className="App">
      <audio src={require('./assets/sound/sappheiros-embrace.mp3')}></audio>
      <StarBackground />

      <header>

        { location !== '/'  &&

            <Link to='/' className='headerbutton'>
              <span className="material-icons">arrow_back</span>
            </Link>
        }

        <div className='headerbutton playMusic' onClick={() => toggleMusic(musicPlays)}>
            <span className="material-icons">music_off</span>
        </div>

        { location === '/game'  &&
          <div className='headerbutton pauseGame'>
              <span className="material-icons">pause</span>
          </div>
        } 

      </header>

      <Switch>
        <Route exact path="/" children={<Home />} />
        <Route path="/game" children={<Game />} />
        <Route path="/options" children={<Options />} />
        <Route path="/credits" children={<Credits />} />
      </Switch>

      <footer>
          <a href='https://github.com/KevvKo/jump-and-run-js'>@KevvKo</a>
        </footer>
    </div>
  );
}

export default App;
