import { SCALE_WIDTH } from "../constants/action-types";
import { SCALE_HEIGHT } from "../constants/action-types";

const initialState= {
    width: 0,
    height: 0
}

function canvasReducer( state = initialState, action) {

    if(action.type === SCALE_WIDTH){
        
        const canvas = document.getElementById('gameBoard')
        const width = window.innerWidth
        const height = window.innerHeight
        const header = document.querySelector('header')
        
        return {
            ...state,
            width: state.width
        }
    }

    if(action.type === SCALE_HEIGHT){

        return {
            ...state,
            height: state.height
        }
    }

    return state;
}

export default canvasReducer
