import React, { useState } from 'react';

import menuTrack1 from '../sound/menu/sappheiros-embrace.mp3'
import menuTrack2 from '../sound/menu/Go Go Go - Kwon.mp3'
import menuTrack3 from '../sound/menu/Ocean View - Patrick Patrikios.mp3'
import menuTrack4 from '../sound/menu/Polyphonic Congas - Unicorn Heads.mp3'
import menuTrack5 from '../sound/menu/Space Station Experience - Unicorn Heads.mp3'

export function Music() {

    const menuTrac = [
        {
            "artist"    :   "sappheiros",
            "track"     :   "embrace",
            "file"      :   menuTrack1
        },
        {
            "artist"    :   "Known",
            "track"     :   "Go Go Go",
            "file"      :   menuTrack2
        },
        {
            "artist"    :   "Patrick Patrikios",
            "track"     :   "Ocean View",
            "file"      :   menuTrack3
        }, 
        {
            "artist"    :   "Unicorn Heads",
            "track"     :   "Polyphonic Congas",
            "file"      :   menuTrack4
        }, 
        {
            "artist"    :   "Unicorn Heads",
            "track"     :   "Space Station Experience",
            "file"      :   menuTrack5
        }
    ]

    const musicIsPlaying =  null

    const [musicPlays, setMusicPlays] = useState(false)

    const toggleMusic = () => {

      let button = document.querySelector('.playMusic')
      let audioElement = document.querySelector('audio')

      if(musicPlays){
        audioElement.pause()
        button.children[0].innerText = 'music_off'
        setMusicPlays(false)

      }else{
        audioElement.play()
        button.children[0].innerText = 'music_note'
        setMusicPlays(true)
      }
    }
}
