import {createSlice} from '@reduxjs/toolkit'

export const canvasScaleSlice = createSlice({
    name: 'canvasScaler',
    initialState: {
        width: 0,
        height: 0
    },
    reducers: {

        scaleWidth: state => {
            state.width = window.innerWidth
        },

        scaleHeight: (state, action) => {
            state.height = action.height
        }
    }
})

export const { scaleWidth, scaleHeight} = canvasScaleSlice.actions
export default canvasScaleSlice.reducer
