import { game } from '../../scripts/game'

const initialState= {
    gameIsPaused: false
}

export default function gameReducer( state = initialState, action) {

    if(action.type === 'game/PauseGame'){
        return{
            ...state,
            loop: action.payload
        }
    }

    return state;
}
