import { game } from '../../scripts/game'

const initialState= {
    gameIsPaused: false
}

export default function gameReducer( state = initialState, action) {

    if(action.type === 'game/PauseGame'){
        const t = {...state}


        console.log(t)
        console.log('he')
        return t
    }

    if(action.type === 'game/ContinueGame'){
        return{
            ...state,
            gameIsPaused: false
        }
    }

    return state;
}
