import {game} from './game'

function isDown(direction){

    return direction.key  === 'w' ||  's'
}

export function handleInput(e){

    switch(e.key) {
    // UP

    case 'w':
        game.spaceship.y =  game.spaceship.y -5
        break

    // DOWN
    case 's':
        game.spaceship.y =  game.spaceship.y +1
        break

    // LEFT
    case 'a':
        game.spaceship.x =  game.spaceship.x -1
        break

    // RIGHT
    case 'd':
        game.spaceship.x =  game.spaceship.x +1
        break
    }
}
