import React, { useEffect } from 'react'
import './play.css'

import {game} from '../assets/js/game'

import spaceship from '../assets/img/spaceship.png'
import spaceship2 from '../assets/img/spaceship2.png'
import spaceship3 from '../assets/img/spaceship3.png'
import spaceship4 from '../assets/img/spaceship4.png'

export default function Play(){


    useEffect(() => {

        scaleCanvas()
        game.init()

        return () => {
            game.stop()
        }
    });
    
    const scaleCanvas = () => {
        
        const canvas = document.getElementById('gameBoard')
        const width = window.innerWidth
        const height = window.innerHeight
        const header = document.querySelector('header')
        
        canvas.width = width
        canvas.height = height - header.offsetHeight - 6
    };

    return (
        <div className='play'>
            <img src={spaceship} alt="spaceship" />
            <img src={spaceship2} alt="spaceship2" />
            <img src={spaceship3} alt="spaceship3" />
            <img src={spaceship4} alt="spaceship4" />
            <canvas id='gameBoard'></canvas>
        </div>
    )
}
